'use strict'

/**
 * Module dependencies.
 */

import { define, radians } from './utils'
import { Object3DCommand } from './object3d'
import { registerStat } from './stats'
import { Vector } from './math'
import coalesce from 'defined'
import mat4 from 'gl-mat4'
import vec3 from 'gl-vec3'
import quat from 'gl-quat'

// Scratch matrix
const scratch = mat4.identity([])

/**
 * CameraCommand constructor.
 * @see CameraCommand
 */

module.exports = exports = (...args) => new CameraCommand(...args)

export const DEFAULT_CAMERA_FAR = 1000.0
export const DEFAULT_CAMERA_NEAR = 0.01
export const DEFAULT_CAMERA_FIELD_OF_VIEW = radians(60)
export const DEFAULT_CAMERA_ORIENTATION_ORIGIN = Object.freeze(
  // pitch, yaw, roll
  new Vector(radians(90), radians(0), radians(0))
)

export class CameraCommand extends Object3DCommand {
  constructor(ctx, initialState = {}) {
    registerStat('Camera')
    const worldUp = new Vector(0, 1, 0)
    const target = initialState.target || new Vector(0, 0, 0)

    const right = new Vector(0, 0, 0) // computed
    const front = new Vector(0, 0, 0) // computed
    const eye = new Vector(0, 0, 0) // computed
    const up = new Vector(0, 0, 0) // computed

    const orientation = [...DEFAULT_CAMERA_ORIENTATION_ORIGIN]
    const projection = mat4.identity([])
    const view = mat4.identity([])

    let near = coalesce(initialState.near, DEFAULT_CAMERA_NEAR)
    let far = coalesce(initialState.far, DEFAULT_CAMERA_FAR)
    let fov = coalesce(initialState.fov, initialState.fieldOfView, DEFAULT_CAMERA_FIELD_OF_VIEW)

    const context = {
      projection: () => projection,
      transform: () => mat4.identity([]),
      aspect: ({viewportWidth, viewportHeight}) => viewportWidth/viewportHeight,
      view: () => view,
      fov: () => fov,
    }

    const uniforms = {
      projection: () => projection,
      aspect: ({viewportWidth, viewportHeight}) => viewportWidth/viewportHeight,
      view: computeViewMatrix,
      eye: () => [...eye],

    }

    const injectContext = ctx.regl({
      context,
      uniforms
    })

    super(ctx, {
      ...initialState,
      transform: false,
      update(state, block) {
        injectContext(state, block)
      }
    })

    function computeViewMatrix({
      viewportWidth,
      viewportHeight,
      position,
      rotation,
      scale
    }, state) {
      state = state || {}

      if ('fov' in state) {
        fov = state.fov
      }

      if ('far' in state) {
        far = state.far
      }

      if ('near' in state) {
        near = state.near
      }

      if ('orientation' in state) {
        vec3.copy(orientation, state.orientation)
      }

      if ('target' in state) {
        vec3.copy(target, state.target)
      }

      if ('worldUp' in state) {
        vec3.copy(worldUp, state.worldUp)
      }

      const aspect = viewportWidth/viewportHeight

      if (!position || !rotation || !scale) { return }

      // compute front vector from orientation euler
      vec3.set(front,
        Math.cos(orientation[0]) * Math.cos(orientation[1]),
        Math.sin(orientation[1]),
        Math.sin(orientation[0]) * Math.sin(orientation[1])
      )

      // normalize front vector and compute
      // corresponding right and worldUp vectors
      vec3.normalize(front, front)
      vec3.copy(right, vec3.normalize([], vec3.cross([], front, worldUp)))
      vec3.copy(up, vec3.normalize([], vec3.cross([], right, front)))

      // set projection
      // @TODO(werle) - mat4.orhto()
      mat4.perspective(projection, fov, aspect, near, far)

      // update view matrix with scaled
      // position and target vectors
      const center = vec3.multiply([], position || [0, 0, 0], scale)
      mat4.lookAt(view, center, vec3.multiply([], target, scale), up)

      // scale and rotate view matrix
      mat4.multiply(view, view, mat4.fromQuat([], rotation))
      mat4.scale(view, view, scale)

      // compute eye vector from the inverse view matrix
      mat4.invert(scratch, view)
      vec3.set(eye, scratch[12], scratch[13], scratch[14])

      return view
    }
  }
}

'use strict'

const encode = (fn) => `(${String(fn)})`
const noop = () => this

module.exports = exports = (...args) => new Command(...args)

export class Command extends Function {
  constructor(run) {
    super(codegen(commandRunnerWrap))
    run = 'function' == typeof run ? run : noop
    const state = {_run: run}
    const exec = (...args) => this(run, ...args)
    const self = this
    return (...args) => exec.call(self, ...args)
  }
}

function codegen(fn) {
  return `return ${encode(fn)}.apply(this, arguments);`
}

function commandRunnerWrap(run, ...args) {
  if (this && 'function' == typeof run) {
    return run.apply(run, args)
  }
  return this
}

'use strict'

/**
 * Module dependencies.
 */

import { registerStat } from './stats'
import { EventEmitter } from 'events'

import events from 'dom-events'
import glsl from 'glslify'
import regl from 'regl'

export const defaults = {
  clear: {
    // @TODO(werle) - use a color module
    color: [17/255, 17/255, 17/255, 1],
    depth: 1,
  },
}

module.exports = exports = (state, opts) =>
  new Context({...defaults, ...state}, opts)

export class Context extends EventEmitter {
  constructor(initialState = {}, opts = {}, createRegl = regl) {
    registerStat('Context')
    super()

    this._stack = []
    this._state = { ...initialState }
    this._caller = null
    this._scope = null
    this._hasFocus = false
    this._reglContext = null

    this.setMaxListeners(Infinity)
    opts.regl = opts.regl || opts.gl || {}

    if (opts.element && 'CANVAS' == opts.element.nodeName) {
      opts.regl.canvas = opts.element
    } else if (opts.element && opts.element.nodeName) {
      opts.regl.container = opts.element
    } else if ('string' == typeof opts.element) {
      opts.regl.container = opts.element
    }

    createRegl({
      ...(opts.regl),

      attributes: {
        ...(opts.regl.attributes || {}),
      },

      extensions: [
        ...(opts.regl.extensions || []),
        'OES_texture_float',
      ],

      optionalExtensions: [
        ...(opts.regl.optionalExtensions || []),
      ],

      onDone: (err, regl) => {
        if (err) {
          return this.emit('error', err)
        }

        this._regl = regl
        this._domElement = this._regl._gl.canvas
        this._isDestroyed = false
      }
    })

    // DOM events
    events.on(this._domElement, 'focus', () => this.focus())
    events.on(this._domElement, 'blur', () => this.blur())
    events.on(window, 'blur', () => this.blur())
  }

  get reglContext() { return this._reglContext }
  get domElement() { return this._domElement }
  get hasFocus() { return this._hasFocus }
  get caller() { return this._caller }
  get scope() { return this._scope }
  get depth() { return this._stack.length }
  get stack() { return this._stack }
  get regl() { return this._regl }

  /**
   * Focuses context.
   *
   * @return {Context}
   */

  focus() {
    this._hasFocus = true
    this.emit('focus')
    return this
  }

  /**
   * Blurs context.
   *
   * @return {Context}
   */

  blur() {
    this._hasFocus = false
    this.emit('blur')
    return this
  }

  /**
   * Pushes command to context stack.
   *
   * @param {Command} command
   * @return {Context}
   */

  push(command) {
    if ('function' == typeof command) {
      this._scope = this._stack[this._stack.length - 1]
      this._stack.push(command)
      this._caller = command
    }
    return this
  }

  /**
   * Pops tail of context command stack.
   *
   * @return {Context}
   */

  pop() {
    let command = this._stack.pop()
    this._caller = this._stack[this._stack.length - 1]
    this._scope = command
    return command
  }

  /**
   * Updates command context state.
   *
   * @param {Object} state
   * @return {Context}
   */

  update(state) {
    if (state && 'object' == typeof state) {
      Object.assign(this._state, state)
    }
    return this
  }

  /**
   * Resets context state.
   *
   * @return {Context}
   */

  reset() {
    this._caller = null
    this._scope = null
    this._stack.splice(0, this._stack.length)
  }

  /**
   * @TODO(werle) - move this Frame as a context function
   * Clears the clear buffers in regl.
   *
   * @return {Context}
   */

  clear() {
    if (this._regl && this._state) {
      this._regl.clear(this._state.clear)
    }
    return this
  }

  /**
   * Destroys the context and the
   * regl context associated with it.
   *
   * @return {Context}
   */

  destroy() {
    this.clear()

    if (this._regl && 'function' == typeof this._regl.destroy) {
      this._regl.destroy()
    }

    this._state = {}
    this._stack = []
    this._hasFocus = false

    delete this._regl
    delete this._scope
    delete this._caller
    delete this._domElement
    delete this._reglContext
    return this
  }
}

'use strict'

/**
 * Module dependencies.
 */

import {
  Command
} from './command'

import {
  define
} from './utils'

import {
  incrementStat,
  registerStat,
  resetStat
} from './stats'

import glslify from 'glslify'

const kDefaultFragmentShader =
  glslify(__dirname + '/glsl/material/fragments/main.glsl', {
    transform: ['glslify-fancy-imports']
  })

module.exports = exports = (...args) => new FrameCommand(...args)
export class FrameCommand extends Command {
  constructor(ctx) {
    incrementStat('Frame')
    const {regl} = ctx
    const queue = []
    const lights = []

    let reglContext = null
    let isRunning = false
    let tick = null

    const injectContext = regl({
      frag: kDefaultFragmentShader,
      context: {
        resolution: ({viewportWidth: w, viewportHeight: h}) => ([w, h]),
        lights: () => lights
      },

      uniforms: {
        time: ({time}) => time,
      },

      blend: {
        equation: 'add',
        enable: true,
        color: [0, 0, 0, 1],
        func: {src: 'src alpha', dst: 'one minus src alpha'},
      },

      cull: {
        enable: true,
        face: 'back',
      },

      depth: {
        enable: true,
        range: [0, 1],
        mask: true,
        func: 'less',
      }
    })

    super((refresh) => {
      queue.push(refresh)
      registerStat('Frame refresh')
      frame()
      return cancel
    })

    //
    // cancels regl animation frame loop,
    // resets state, and removes the caputured
    // regl context from the axis context
    //
    function cancel() {
      if (tick) {
        queue.splice(0, -1)
        tick.cancel()
        ctx._reglContext =
        reglContext =
        tick = null
      }
    }

    //
    // Function to handle regl animation
    // frame loop logic that captures the regl context
    // and stores it on the axis context. It also
    // clears the drawing canvas, resets the axis context,
    // and calls all queued frame refresh functions
    //
    function frame() {
      if (false == isRunning) {
        isRunning = true
        tick = regl.frame((_, ...args) => {
          reglContext = _
          ctx._reglContext = reglContext
          const {
            viewportWidth: width,
            viewportHeight: height,
          } = reglContext

          try {
            injectContext((_) => {
              ctx.reset()
              ctx.clear()
              lights.splice(0, lights.length)
              for (let refresh of queue) {
                if ('function' == typeof refresh) {
                  refresh(reglContext, ...args)
                }
              }
            })
          } catch (e) {
            ctx.emit('error', e)
            cancel()
          }
        })
      }
    }
  }
}

