/**
 * Module dependencies.
 */

import { define } from '../utils'
import coalesce from 'defined'
import vec4 from 'gl-vec4'
import vec3 from 'gl-vec3'
import vec2 from 'gl-vec2'

export class Vector {
  constructor(...input) {
    if (1 == input.length && 'object' == typeof input[0]) {
      const tmp = input[0]
      input[0] = tmp.x || tmp[0] || undefined
      input[1] = tmp.y || tmp[1] || undefined
      input[2] = tmp.z || tmp[2] || undefined
      input[3] = tmp.w || tmp[3] || undefined
      input = input.filter((x) => undefined !== x)
    }

    const elements = new Float32Array([...input])
    const mappings = [
      [0, 1, 2, 3],
      ['x', 'y', 'z', 'w'],
      ['r', 'g', 'b', 'a'],
    ]

    for (let mapping of mappings) {
      for (let i = 0; i < mapping.length; ++i) {
        if (null == elements[i]) { break }
        define(this, mapping[i], {
          enumerable: true,
          set: (v) => elements[i] = v,
          get: () => elements[i],
        })
      }
    }

    define(this, 'elements', { get:() => elements })
  }

  /**
   * Vector length getter.
   *
   * @type {Number}
   * @public
   */

  get length() {
    return this.elements.length
  }

  /**
   * Dummy length setter.
   *
   * @private
   */

  set length(value) {
    void value
  }

  /**
   * Returns a reference to the underlying
   * vector elements.
   *
   * @getter
   * @type {Float64Array}
   */

  get ref() {
    return this.elements
  }

  /**
   * Returns the component count of the vector
   *
   * @getter
   * @type {Number}
   */

  get componentLength() {
    return this.elements.length
  }

  /**
   * Set components-wise values
   *
   * @param {Number} x
   * @param {Number} y
   * @param {Number} z
   * @param {Number} w
   * @return {Vector}
   */

  set(x, y, z, w) {
    if (x instanceof Vector) {
      return this.set(x.x, x.y, x.z, x.w)
    }

    // switch fall through component setter
    switch (arguments.length) {
      case 4: this.elements[3] = coalesce(w, this.elements[3]);
      case 3: this.elements[2] = coalesce(z, this.elements[2]);
      case 2: this.elements[1] = coalesce(y, this.elements[1]);
      case 1: this.elements[0] = coalesce(x, this.elements[0]);
    }
    return this
  }

  /**
   * Converts the vector into
   * a normal Array.
   *
   * @return {Array}
   */

  toArray() {
    return [...this.elements]
  }

  /**
   * Returns a JSON serializable value.
   *
   * @return {Array}
   */

  toJSON() {
    return this.toArray()
  }

  /**
   * Returns the underlying vector
   * array value.
   *
   * @return {Float64Array}
   */

  valueOf() {
    return this.elements
  }

  /**
   * Iterator protocol implementation.
   */

  [Symbol.iterator]() {
    return this.toArray()[Symbol.iterator]()
  }
}

'use strict'

/**
 * Module dependencies.
 */

import { Object3DCommand } from './object3d'
import { incrementStat } from './stats'
import getBoundingBox from 'bound-points'
import injectDefines from 'glsl-inject-defines'
import { Geometry } from './geometry'
import { define } from './utils'
import coalesce from 'defined'
import glslify from 'glslify'
import clamp from 'clamp'
import mat4 from 'gl-mat4'
import mat3 from 'gl-mat3'
import vec4 from 'gl-vec4'
import vec3 from 'gl-vec3'
import vec2 from 'gl-vec2'
import quat from 'gl-quat'

import {
  Quaternion,
  Vector,
} from './math'

const identity = mat4.identity([])

const kDefaultMeshWireframePrimitive = 'line strip'
const kDefaultWireframeThickness = 1
const kDefaultMeshPrimitive = 'triangles'
const kDefaultVertexShader = glslify(__dirname + '/glsl/mesh/vert.glsl')

module.exports = exports = (...args) => new MeshCommand(...args)
export class MeshCommand extends Object3DCommand {
  constructor(ctx, initialState = {}) {
    incrementStat('Mesh')

    let {
      regl: reglOptions = {},
      vert: vertexShader = kDefaultVertexShader,
      frag: fragmentShader = null,

      primitive = 'triangles',
      geometry = null,
    } = initialState

    // computed bounding box that is computed once
    // and cached in this variable
    let boundingBox = null

    // draw function created by regl
    // if a geometry is given
    let draw = () => void 0

    // initial configurable state
    const initial = {
      wireframeThickness: (
        initialState.wireframeThickness
        || kDefaultWireframeThickness
      ),

      wireframe: Boolean(initialState.wireframe),
      count: initialState.count || undefined
    }

    const attributes = {}

    // shader uniforms
    const uniforms = {
      model: ({transform}) => transform || identity,
      modelNormal: ({transform}, {} = {}, id) => {
        return (
          transform ?
          mat3.normalFromMat4([], transform) :
          identity
        )
      },

      // at least enough for flat shading incase a material isn't given
      'material.opacity': ({materialOpacity}, {opacity = materialOpacity} = {}) => opacity,
      'material.color': ({materialColor}, {color = materialColor} = {}) => color,
    }

    // regl context
    const context = {
      ...reglOptions.context,

      boundingBox: computeBoundingBox,
      geometry: geometry || null,
      size: computeSize,
    }

    Object.assign(reglOptions, {context})

    // configure vertex shader if a geometry with a
    // simpicial complex is given
    if (geometry) {
      Object.assign(attributes, initialState.attributes)
      Object.assign(uniforms, initialState.uniforms)
      Object.assign(reglOptions, {
        attributes,
        uniforms,
      })

      // shader defines injected into vertex shader
      const shaderDefines = {
        ...initialState.shaderDefines
      }

      if (geometry.positions) {
        attributes.position = geometry.positions
        shaderDefines['HAS_POSITIONS'] = 1
      }

      if (geometry.normals) {
        attributes.normal = geometry.normals
        shaderDefines['HAS_NORMALS'] = 1
      }

      if (geometry.uvs) {
        attributes.uv = geometry.uvs
        shaderDefines['HAS_UVS'] = 1
      }

      Object.assign(reglOptions, {
        primitive: geometry && (({}, {
          primitive: inputPrimitive = null,
          wireframe = false,
        } = {}) => {
          if (inputPrimitive) {
            return inputPrimitive
          } else if (wireframe) {
            return kDefaultMeshWireframePrimitive
          } else if ('string' == typeof primitive) {
            return primitive
          } else {
            return kDefaultMeshPrimitive
          }
        })
      })

      if (geometry.cells) {
        Object.assign(reglOptions, {
          elements: ({}, {
            wireframe = initial.wireframe,
            count = initial.count,
          } = {}) => {
            let cells = geometry.cells
            if (cells && 'number' == typeof count) {
              count = coalesce(count, cells.length)
              count = clamp(Math.floor(count), 0, cells.length)
              cells = cells.slice(0, count)
            }
            return cells
          }
        })
      } else {
        Object.assign(reglOptions, {
          count: ({}, {count} = {}) => {
            if (null != count) { return count }
            else if (initialState.count) { return initialState.count }
            else if (geometry && geometry.complex) {
              return geometry.positions.length
            } else {
              return null
            }
          }
        })
      }

      if ('string' == typeof vertexShader) {
        reglOptions.vert = injectDefines(vertexShader, shaderDefines)
      }

      if ('string' == typeof fragmentShader) {
        reglOptions.frag = injectDefines(fragmentShader, shaderDefines)
      }

      for (let key in reglOptions) {
        if (undefined === reglOptions[key]) {
          delete reglOptions[key]
        }
      }

      if (geometry) {
        draw = ctx.regl({
          lineWidth: ({}, {
            wireframeThickness = initial.wireframeThickness,
            lineWidth = 1,
          } = {}) => {
            return Math.max(wireframeThickness || lineWidth, 1)
          },
          ...reglOptions
        })
      }
    }

    super(ctx, {
      ...initialState,

      // Draws mesh and with given state and
      // then calling an optional given block
      update(state, block) {
        const noop = () => void 0

        if ('function' == typeof state) {
          block = state
          state = {}
        }

        state = state || {}
        block = block || noop

        // helper function to call hook defined on
        // input state falling back to initial state
        const hook = (name) =>
          void (state[name] || initialState[name] || noop)(state)

        const update = initialState.update || (({} = {}, f) => f())

        update(state, () => {
          if (null == ctx.reglContext) {
            block({ ...(ctx.reglContext || {}) })
          } else {
            ctx.reglContext.geometry = geometry
            if (false === state.visible) {
              draw(state, block)
            } else if (false != state.draw) {
              hook('beforeDraw')
              draw(state)
              hook('afterDraw')
              block({ ...ctx.reglContext }, state)
            } else {
              draw(state, block)
            }
            delete ctx.reglContext.geometry
          }
        })
      }
    })

    //
    // Function to compute the bounding box of the internal
    // geometry. The bounding box is computed once and cached.
    //
    function computeBoundingBox({}, {} = {}) {
      return () => {
        if (geometry && null == boundingBox) {
          boundingBox = (
            getBoundingBox(geometry.positions)
            .map((p) => new Vector(...p))
          )
        }

        return boundingBox
      }
    }

    //
    // Function to compute the size of the geometry with
    // respect to scaling and dimension
    //
    function computeSize({scale} = {}, {} = {}) {
      return () => {
        if (null == geometry) {
          return null
        }

        computeBoundingBox()

        const dimension = boundingBox[0].length
        const min = boundingBox[0]
        const max = boundingBox[1]
        let size = null

        switch (dimension) {
          case 3: size = vec3.subtract([], max, min); break
          case 2: size = vec2.subtract([], max, min); break
          default: return null
        }

        switch (dimension) {
          case 3: return vec3.multiply(size, size, scale)
          case 2: return vec2.multiply(size, size, scale)
        }
      }
    }
  }
}

'use strict'

/**
 * Module dependencies.
 */

import {
  Quaternion,
  Vector
} from './math'

import {
  incrementStat
} from './stats'

import {
  Command
} from './command'

import {
  define
} from './utils'

import mat4 from 'gl-mat4'
import vec4 from 'gl-vec4'
import vec3 from 'gl-vec3'
import vec2 from 'gl-vec2'
import quat from 'gl-quat'

let OBJECT_COMMAND_COUNTER = 0

module.exports = exports = (...args) => new Object3DCommand(...args)
export class Object3DCommand extends Command {
  //
  // Returns the next object ID
  //
  static id() {
    return OBJECT_COMMAND_COUNTER ++
  }

  constructor(ctx, initialState = {}) {
    incrementStat('Object3D')

    const {
      transform: wantsTransform = true,
      update = function () {},
      type = 'object',
      id = Object3DCommand.id(),

      rotation: initialRotation = [0, 0, 0, 1],
      position: initialPosition = [0, 0, 0],
      scale: initialScale = [1, 1, 1],
    } = initialState

    let didJustComputeTransfromFromInitialState = false
    const transform = mat4.identity([])
    const local = mat4.identity([])

    // regl context
    const injectContext = ctx.regl({
      context: {
        id: () => id,
        scale: ({}, {scale = initialScale} = {}) => scale,
        position: ({}, {position = initialPosition} = {}) => position,
        rotation: ({}, {rotation = initialRotation} = {}) => rotation,
        transform: ({transform: parentTransform}, args = {}, batchId) => {
          if (false === wantsTransform) {
            return undefined
          }

          const {
            scale = initialScale,
            position = initialPosition,
            rotation = initialRotation,
          } = args

          // create copy in case for nested cycles
          if (parentTransform) {
            parentTransform = [ ...parentTransform ]
          } else {
            parentTransform = mat4.identity([])
          }

          if (scale == initialScale &&
              position == initialPosition &&
              rotation == initialRotation &&
              didJustComputeTransfromFromInitialState) {
            return mat4.multiply(transform, parentTransform, local)
          }

          mat4.identity(local)
          mat4.identity(transform)

          // M = T * R * S
          mat4.translate(local, local, position)
          mat4.multiply(local, local, mat4.fromQuat([], rotation))
          mat4.scale(local, local, scale)

          if (scale == initialScale &&
              position == initialPosition &&
              rotation == initialRotation) {
            didJustComputeTransfromFromInitialState = true
          } else {
            didJustComputeTransfromFromInitialState = false
          }

          // M' = Mp * M
          mat4.multiply(transform, parentTransform, local)

          return transform
        },
      }
    })

    // calls current target render function
    super((state = {}, block = () => void 0) => {
      if ('function' == typeof state) {
        block = state
        state = {}
      }

      state = state || {}
      block = block || function() {}

      // inject context suitable for
      // all Object3DCommand instances
      injectContext(state, ({}, args = {}) => {
        update(args, block)
      })
    })

    //
    // Public read only properties
    //
    define(this, 'id', { get() { return id } })
  }
}

'use strict'

export const stats = {}

export function createStatList(name) {
  if (null == stats[name]) {
    stats[name] = []
  }
  return stats[name]
}

export function registerStat(name, info) {
  createStatList(name)
  const stat = new Stat(name, info)
  stats[name].push(stat)
  return stat
}

export function incrementStat(name) {
  createStatList(name)
  const tail = tailStat(name)
  let value = 1
  if (tail) {
    value = tail.info + 1
  }
  registerStat(name, value)
  return value
}

export function decrementStat(name) {
  createStatList(name)
  const tail = tailStat(name)
  let value = 0
  if (tail) {
    value = tail.info - 1
  }
  registerStat(name, value)
  return value
}

export function resetStat(name) {
  stats[name] = []
}

export function headStat(name) {
  if (stats[name]) {
    return stats[name][0] || null
  }
  return null
}

export function tailStat(name) {
  if (stats[name]) {
    const len = stats[name].length || 0
    return stats[name][len - 1] || null
  }
  return null
}

export class Stat {
  constructor(name, info) {
    this.name = name
    this.info = info
    this.timestamp = Date.now()
  }
}

'use strict'

/**
 * Module dependencies.
 */

import { incrementStat } from './stats'
import { Command } from './command'

module.exports = exports = (...args) => new TextureCommand(...args)
export class TextureCommand extends Command {
  constructor(ctx, opts = {}) {
    incrementStat('Texture')

    const {regl} = ctx

    // predicate helpers
    const isCanvas = (d) => d instanceof HTMLCanvasElement
    const isVideo = (d) => d instanceof HTMLVideoElement
    const isImage = (d) => d instanceof HTMLImageElement

    //
    // predicate helper function to determine if
    // given data is ready for upload
    //
    function isDataReady(data) {
      if (!data) { return false }
      if (isVideo(data)) {
        if (data.readyState < data.HAVE_ENOUGH_DATA) {
          return false
        }
      } else if (isImage(data) || isCanvas(data)) {
        if (!data.width || !data.height) {
          return false
        }
      }
      return true
    }

    //
    // Helper function to return a 2d vector
    // representing the texture data resolution
    //
    function getTextureResolution() {
      if (isImage(data) || isCanvas(data)) {
        return [data.width, data.height]
      } else if (isVideo(data)) {
        return [data.videoWidth, data.videoHeight]
      } else if (data && data.shape) {
        return data.shape
      } else if (textureState.shape) {
        return textureState.shape
      } else if (textureState.width && textureState.height) {
        return [textureState.width, textureState.height]
      } else {
        return [0, 0]
      }
    }

    // texture state used for in place regl
    // texture reinitialization
    let textureState = {
      min: 'linear',
      mag: 'linear',
      ...opts
    }

    // initial texture state that is used to reset
    // the texture state for the regl texture
    const initialTextureState = Object.freeze({
      ...textureState
    })

    // remove data from initial texture state if it is
    // not a texture valid shape
    if (textureState.data) {
      const {data} = textureState
      if (false == isDataReady(textureState.data)) {
        delete textureState.data
      }
    }

    // wrapped regl texture initialized without data
    const texture = regl.texture(textureState)

    // regl context
    const injectContext = regl({
      context: {
        textureResolution: () => getTextureResolution(),
        textureData: () => data,
        texture: () => texture,
      }
    })

    // data state
    let lastVideoRefresh = 0
    let data = textureState.data
    let previouslyUploadedData = null
    let needsUpdate = false

    // texture update function
    super((state, block) => {
      const noop = () => void 0
      const now = regl.now()

      if ('function' == typeof state) {
        block = state
        state = {}
      }

      state = state || {}
      block = block || noop

      // potential texture state if an updated is needed
      textureState = {
        ...initialTextureState
      }

      needsUpdate = false

      // try to determine if an update is needed from user
      // state input
      for (let key in state) {
        if ('object' != typeof state[key] &&
            textureState[key] != state[key]) {
          needsUpdate = true
        }
        textureState[key] = state[key]
      }

      if ('data' in state) {
        if (data != state.data) {
          data = state.data
          needsUpdate = true
        }
      }

      // texture data is set from user state input
      // falling back on the initial texture data
      // provided as a default
      if (null == data && initialTextureState.data) {
        if (isDataReady(initialTextureState.data)) {
          data = initialTextureState.data
          needsUpdate = true
        }
      }

      // previous call had texture data to upload, but
      // the current does not and the texture was not
      // initialized with default texture data
      if (null == data &&
          null == initialTextureState.data &&
          null != previouslyUploadedData) {
        needsUpdate = true
      }

      // video data from a video element is streaming
      // and should always be updated
      if (false == needsUpdate && isVideo(data)) {
        if (data.readyState >= data.HAVE_CURRENT_DATA) {
          if (now - lastVideoRefresh >= 0.01) {
            lastVideoRefresh = now
            needsUpdate = true
          }
        }
      }

      if (false == needsUpdate) {
        // computed texture data resolution
        const resolution = getTextureResolution()
        // mark for update if resolution is available and
        // the previously uploaded texture data defers from
        // the current input data
        if (resolution[0] > 0 && resolution[1] > 0) {
          if (data != previouslyUploadedData) {
            needsUpdate = true
          }
        }
      }

      // update regl rexture state and set
      if (needsUpdate) {
        previouslyUploadedData = data
        textureState.data = data
        texture(textureState)
        needsUpdate = false
      }

      // inject texture context exposing useful
      // texture state variables
      injectContext(block)
    })
  }
}

'use strict'

/**
 * Module dependencies.
 */

import { version } from './package'
import createDebug from 'debug'

/**
 * Math dependencies.
 */

const { round, floor, pow, } = Math

/**
 * Define property helper.
 *
 * @public
 * @param {Object} a
 * @param {String} b
 * @param {Object} c
 */

export const define = (a, b, c) => Object.defineProperty(a, b, { ...c })

/**
 * Converts input degrees to radians
 *
 * @public
 * @param {Number} n
 * @return {Number}
 */

export const radians = (n) => n == n ? (n*Math.PI/180.0) : 0

/**
 * Utility debug output
 *
 * @public
 * @param {String} fmt
 * @param {...Mixed} args
 */

export const debug = createDebug(`[axis@${version}]`)

/**
 * Simple linear inerpolation function.
 *
 * @public
 * @param {Number} v0
 * @param {Number} v1
 * @param {Number} t
 * @return {Number}
 */

export const lerp = (v0, v1, t) => v0*(1 - t) + v1*t

/**
 * Predicate function to determine if a given DOM
 * element is in the window's viewport.
 *
 * @public
 * @param {Element} domElement
 * @return {Boolean}
 */

export const isDOMElementInViewport = (domElement) => {
  const {clientWidth, clientHeight} = document.documentElement
  const {top, left, bottom, right} = domElement.getBoundingClientRect()
  const {innerWidth, innerHeight} = window
  const height = innerHeight || clientHeight
  const width = innerWidth || clientWidth
  return bottom > 0 && right > 0 && left < width && top < height
}

/**
 * Returns the screen orientation angle.
 * Borrowed from https://github.com/hawksley/eleVR-Web-Player/blob/master/lib/util.js
 *
 * @return {Number}
 */

export const getScreenOrientation = () => {
  switch (window.screen.orientation || window.screen.mozOrientation) {
    case 'landscape-primary': return 90
    case 'landscape-secondary': return -90
    case 'portrait-secondary': return 180
    case 'portrait-primary': return 0
  }

  return window.orientation || 0
}

/**
 * Finds the nearest power of two for a
 * given number value.
 */

export const nearestPowerOfTwo = (value) => pow(2, round(Math.log(value) / Math.LN2))

/**
 * Convert an image or canvas to the nearest power
 * of two.
 * Borrowed from https://github.com/mrdoob/three.js/blob/dev/src/renderers/webgl/WebGLTextures.js
 *
 * @param {HTMLImageElement|HTMLCanvasElement} image
 * @return {HTMLImageElement|HTMLCanvasElement}
 */

export const makePowerOfTwo = (image) => {
	if (image instanceof HTMLImageElement || image instanceof HTMLCanvasElement) {
		const canvas = document.createElementNS('http://www.w3.org/1999/xhtml', 'canvas')
		const context = canvas.getContext('2d')

		canvas.width = nearestPowerOfTwo(image.width)
		canvas.height = nearestPowerOfTwo(image.height)
		context.drawImage(image, 0, 0, canvas.width, canvas.height)

		return canvas
	}

	return image
}

/**
 * Creates a canvas DOM element.
 *
 * @return {HTMLCanvasElement}
 */

export const createCanvas = () =>
  document.createElementNS('http://www.w3.org/1999/xhtml', 'canvas')

/**
 * Scale image using a canvas.
 * Warning: Scaling through the canvas will only work with
 * images that use premultiplied alpha.
 *
 * Borrowed from https://github.com/mrdoob/three.js/blob/dev/src/renderers/webgl/WebGLTextures.js
 *
 * @param {HTMLImageElement|HTMLCanvasElement} image
 * @param {Number} maxSize
 * @return {HTMLImageElement|HTMLCanvasElement}
 */

export const scaleWithCanvas = (image, scale, scaleNearestPowerOfTwo) => {
  const canvas = createCanvas()
  const context = canvas.getContext('2d')
  let {width, height} = image

  if (scaleNearestPowerOfTwo) {
    width = nearestPowerOfTwo(width)
    height = nearestPowerOfTwo(height)
  }

  canvas.width = Math.floor(image.width * scale)
  canvas.height = Math.floor(image.height * scale)

  context.drawImage(image, 0, 0, width, height,
                    0, 0, canvas.width, canvas.height)
  return canvas
}

/**
 * Clamp an image to a max size.
 *
 * @param {HTMLImageElement|HTMLCanvasElement} image
 * @param {Number} maxSize
 * @return {HTMLImageElement|HTMLCanvasElement}
 */

export const clampToMaxSize = (image, maxSize, scaleNearestPowerOfTwo) => {
	if (image.width > maxSize || image.height > maxSize) {
    const scale = maxSize/Math.max(image.width, image.height)
    return scaleWithCanvas(image, scale, scaleNearestPowerOfTwo)
  } else {
    return scaleNearestPowerOfTwo ? makePowerOfTwo(image) : image
  }
}
